mapper.update:
update k_data10 
set yydl=#{yydl} , syje=#{syje} , yyje=#{yyje} , jbf=#{jbf} , nlf=#{nlf} 
where yhbh=#{yhbh} and fpdz=#{fpdz}

mapper.select:
select distinct top 500 yh.id, yh.yhbh,yh.yhxm,yh.xqm,yh.ldh,yh.dyh,yh.hh, da.yydl, da.fpbh,da.ms,yh.fpdz,yh.lxdh,yh.bz, da.dw,da.gdtime,da.zdtime,da.ddtime,da.dgdtime,da.dzdtime,da.dddtime, da.jf,da.sdwd,da.snwd,da.kg,da.bj,da.jj,da.time, yh.mj from k_data10 da, k_yhb1 yh, k_fpb2 fp, k_glb6 gl
where yh.yhbh=da.yhbh and yh.fpdz=da.fpdz and da.fpbh=fp.fpbh and fp.xh=gl.xh
and yh.xqm=#{xqm}
and yh.ldh=#{ldh}
and yh.dyh=#{dyh}
and yh.hh=#{hh}
and da.time >= #{time1}
and da.time <= #{time2}
order by da.time desc

select distinct top 500 yh.id, yh.yhbh,yh.yhxm,yh.xqm,yh.ldh,yh.dyh,yh.hh, da.yydl, da.fpbh,da.ms,yh.fpdz,yh.lxdh,yh.bz, da.dw,da.gdtime,da.zdtime,da.ddtime,da.dgdtime,da.dzdtime,da.dddtime, da.jf,da.sdwd,da.snwd,da.kg,da.bj,da.jj,da.time, yh.mj from k_lssjb11 da, k_yhb1 yh, k_fpb2 fp, k_glb6 gl
where yh.yhbh=da.yhbh and yh.fpdz=da.fpdz and da.fpbh=fp.fpbh and fp.xh=gl.xh
and yh.xqm=#{xqm}
and yh.ldh=#{ldh}
and yh.dyh=#{dyh}
and yh.hh=#{hh}
and da.time >= #{time1}
